{"settings":"{
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE DEFAULT: Theme
    "workbench.colorTheme": "Default Light+",
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE DEFAULT: Editor
    "editor.codeLens": false,
    "editor.showFoldingControls": "always",
    "editor.formatOnPaste": true,
    "editor.formatOnSave": true,
    "editor.links": true,
    "html.format.indentInnerHtml": true,
    "html.format.wrapLineLength": 240,
    "html.format.unformatted": "doc.tabler,doc.listr,doc.code",
    "json.format.keepLines": true,
    "php.validate.enable": false,
    "explorer.confirmDragAndDrop": false,
    "explorer.confirmDelete": false,
    "workbench.iconTheme": "vscode-icons",
    "workbench.startupEditor": "none",
    "js/ts.implicitProjectConfig.checkJs": true,
    "css.format.enable": false,
    "git.ignoreMissingGitWarning": true,
    "security.workspace.trust.untrustedFiles": "open",
    "security.workspace.trust.enabled": false,
    "dotnet.codeLens.enableReferencesCodeLens": false,
    "git.openRepositoryInParentFolders": "never",
    "workbench.layoutControl.enabled": false,
    "workbench.navigationControl.enabled": false,
    "workbench.editor.wrapTabs": true,
    "workbench.editor.pinnedTabsOnSeparateRow": true,
    "window.density.editorTabHeight": "compact",
    "workbench.secondarySideBar.defaultVisibility": "hidden",
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Shortcut Menu Bar
    "ShortcutMenuBar.beautify": false,
    "ShortcutMenuBar.openFilesList": false,
    "ShortcutMenuBar.save": true,
    "ShortcutMenuBar.saveAll": true,
    "ShortcutMenuBar.toggleTerminal": false,
    "ShortcutMenuBar.navigateForward": false,
    "ShortcutMenuBar.navigateBack": false,
    "[csharp]": {
        "editor.defaultFormatter": "ms-dotnettools.csharp"
    },
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: VSCode Icons
    "vsicons.dontShowNewVersionMessage": true,
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Path Autocomplete
    "path-autocomplete.excludedItems": {
        "**/*.meta": {
            "when": "**"
        }
    },
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Color Highlight
    "color-highlight.languages": [
        "*"
    ],
    "color-highlight.markRuler": false,
    "color-highlight.matchWords": true,
    "color-highlight.markerType": "underline",
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Color Picker Universal
    "color-picker-universal.avoidDuplicate": true,
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: VSRegex
    "VSRegex.outlineWidth": 1,
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Open In Browser
    "open-in-browser.default": "Microsoft Edge",
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: PHP Intelephense
    "intelephense.completion.maxItems": 125,
    "intelephense.format.enable": false,
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: HTML CSS Support
    "css.enabledLanguages": [ "html", "htm", "php" ],
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Auto Rename Tag
    "auto-rename-tag.activationOnLanguage": [ "*" ],
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: JSON
    "JSON-zain.json.autorefresh": true,
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Text Marker (Highlighter)
    "textmarker.savedHighlights": [
        {
            "pattern": {
                "type": "string",
                "expression": "=>",
                "ignoreCase": false,
                "wholeMatch": false
            },
            "color": "darkgray"
        }
    ],
    "textmarker.commandsOnContextMenu": {
        "updateHighlight": false,
        "clearAllHighlight": false
    },
    "textmarker.defaultHighlightOpacity": 0.3,
    "textmarker.enableTelemetry": false,
    "textmarker.hideStatusBarItems": true,
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Markdown All In One
    "markdown.extension.preview.autoShowPreviewToSide": true,
    "markdown.extension.completion.enabled": true,
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Unity
    "vstuc.displayBackgroundProcesses": true,
    "vstuc.refreshOnSave": true,
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Active File In Status Bar
    "ActiveFileInStatusBar.color": "#B1B1B1",
    "window.zoomLevel": -0.2,
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: VSCode Statusbar Commands
    "statusbarCommands.commands": [
        // More Codicons To Commands At https://code.visualstudio.com/api/references/icons-in-labels
        {
            "statusBarText": "$(settings)",
            "command": "workbench.action.openWorkspaceSettingsFile",
            "tooltipText": "Open the Workspace Settings JSON",
            "color": "#EBEBEB"
        },
        {
            "statusBarText": "$(gear)",
            "command": "workbench.action.openSettingsJson",
            "tooltipText": "Open the VS Code Settings JSON",
            "color": "#EBEBEB"
        },
        {
            "statusBarText": "$(console) Output",
            "command": "workbench.action.output.toggleOutput",
            "tooltipText": "Open the Output",
            "color": "#EBEBEB"
        },
        {
            "statusBarText": "$(chevron-up) Fold Regions",
            "command": "editor.foldAllMarkerRegions",
            "tooltipText": "Fold all Regions in this file...",
            "color": "#EBEBEB"
        },
        {
            "statusBarText": "$(chevron-down) Unfold Regions",
            "command": "editor.unfoldAllMarkerRegions",
            "tooltipText": "Unfold all Regions in this file...",
            "color": "#EBEBEB"
        }
    ],
    "statusbarCommands.alignment": "Left",
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: Project Name in StatusBar
    "projectNameInStatusBar.align": "left",
    "projectNameInStatusBar.alignPriority": 999999,
    "projectNameInStatusBar.textStyle": "uppercase",
    // ───────────────────────────────────────────── SETTINGS FOR VSCODE EXTENSION: MySQL / Database Client JDBC
    "database-client.autoSync": true,
    "database-client.highlightSQLBlock": true,
    "database-client.capitalizeKeywordsWhenFormatting": true,
    "database-client.saveConnectionExpandedState": true
}"}
